package operations.stack.exchange

import java.io.{BufferedInputStream, BufferedReader, InputStream, InputStreamReader}
import java.net.URL
import java.util.zip.GZIPInputStream

import models.{SynonymTagsEdge, Tag, Question}

/**
 * Object used for gzip converting helper functions, as they are needed for extracting data from StackExchange API.
 */
object GZIPDecompressor {
  def toGZIPInputStream(inputStream: InputStream) = new GZIPInputStream(new BufferedInputStream(inputStream))
  def toBufferedReader(gzipInputStream: GZIPInputStream) = new BufferedReader(new InputStreamReader(gzipInputStream))
}

/**
 * Object with HTTP addresses for StackExchange API. Authentication for HTTPS hasn't been done.
 */
object StackExchangeURL {
  /**
   * Base URL for StackExchange
   */
  private val base: String = "http://api.stackexchange.com/2.2"

  /**
   * Get some questions. There are pagination for call.
   * @param page page must be greater than 0
   * @param pageSize pageSize must be less or equal to 100
   * @return string url for this call
   */
  def questions(page: Int = 1, pageSize: Int = 100) = {
    base + "/questions?page=" + page + "&pagesize="+ pageSize + "" +
      "&order=desc&sort=activity&site=stackoverflow"
  }

  /**
   * Get linked questions of specific question. There are pagination for call.
   * @param question_id id of specified question
   * @param page page must be greater than 0
   * @param pageSize pageSize must be less or equal to 100
   * @return string url for this call
   */
  def questionsLinked(question_id: Int, page: Int = 1, pageSize: Int = 100) = {
    base + "/questions/" + question_id + "/linked?page=" + page +
      "&pagesize=" + pageSize + "&order=desc&sort=activity&site=stackoverflow"
  }

  /**
   * Get related questions to specific question. There are pagination for call.
   * @param question_id id of specified question
   * @param page page must be greater than 0
   * @param pageSize pageSize must be less or equal to 100
   * @return string url for this call
   */
  def questionsRelated(question_id: Int, page: Int = 1, pageSize: Int = 100) = {
    base + "/questions/" + question_id + "/related?page=" + page +
      "&pagesize="+ pageSize + "&order=desc&sort=activity&site=stackoverflow"
  }

  /**
   * Get tags.There are pagination for call.
   * @param page page must be greater than 0
   * @param pageSize pageSize must be less or equal to 100
   * @return string url for this call
   */
  def tags(page: Int = 1, pageSize: Int = 100) = {
    base + "/tags?page=" + page + "&pagesize=" + pageSize +
      "&order=desc&sort=popular&site=stackoverflow"
  }

  /**
   * Get Frequently Asked Questions for the specific tag. There are pagination for call.
   * @param tag specified tag
   * @param page page must be greater than 0
   * @param pageSize pageSize must be less or equal to 100
   * @return string url for this call
   */
  def faq(tag: String, page: Int = 1, pageSize: Int = 100) = {
    base + "/tags/" + tag + "/faq?page=" + page + "&pagesize=" + pageSize + "&site=stackoverflow"
  }

  /**
   * Get questions for the specific tag. There are pagination for call.
   * @param tag specified tag
   * @param page page must be greater than 0
   * @param pageSize pageSize must be less or equal to 100
   * @return string url for this call
   */
  def tagBelongs(tag: String, page: Int = 1, pageSize: Int = 100) = {
    base + "/questions?page=" + page + "&pagesize=" + pageSize +
      "&order=desc&sort=activity&tagged=" + tag + "&site=stackoverflow"
  }

  /**
   * Get related tags for the specific tag. There are pagination for call.
   * @param tag specified tag
   * @param page page must be greater than 0
   * @param pageSize pageSize must be less or equal to 100
   * @return string url for this call
   */
  def tagsRelated(tag: String, page: Int = 1, pageSize: Int = 100) = {
    base + "/tags/" + tag + "/related?page=" + page + "&pagesize=" + pageSize + "&site=stackoverflow"
  }

  /**
   * Get synonym tags for the specific tag. There are pagination for call.
   * @param tag specified tags
   * @param page page must be greater than 0
   * @param pageSize pageSize must be less or equal to 100
   * @return string url for this call
   */
  def tagsSynonyms(tag: String, page: Int = 1, pageSize: Int = 100) = {
    base + "/tags/" + tag + "/synonyms?page=" + page + "&pagesize=" + pageSize +
      "&order=desc&sort=creation&site=stackoverflow"
  }
}

/**
 * Object for extracting data from StackExchange API,
 */
object StackExchangeAPIExtractor {

  /**
   * Get json in string format from the specified url from StackExchange API.
   * @param url
   * @return
   */
  def getJson(url: String): String = {
    val inputStream = new URL(url).openStream()
    val gzipInputStream = GZIPDecompressor.toGZIPInputStream(inputStream)
    val bufferedReader = GZIPDecompressor.toBufferedReader(gzipInputStream)
    bufferedReader.readLine()
  }

  /**
   * Extract question from StackExchange API.
   * @param page page must be greater than 0
   * @param pageSize pageSize must be less or equal to 100
   * @return questions
